graphql.query:
query {
  products(filter: { sku: { eq: "221622" } }) {
    items {
      id
      sku
      name
      stock_status
      description {
        html
      }
      short_description {
        html
      }
      special_price
      price_tiers {
        quantity
        discount {
          percent_off
          amount_off
        }
        final_price {
          value
          currency
        }
      }
      price_range {
        minimum_price {
          regular_price {
            value
            currency
          }
        }
      }
      image {
        url
        label
        position
        disabled
      }
      media_gallery_entries {
        file
      }
      only_x_left_in_stock
      ... on ConfigurableProduct {
        configurable_options {
          id
          attribute_code
          label
          values {
            value_index
            label
            swatch_data {
              __typename
              value
            }
          }
        }
        variants {
          product {
            sku
            media_gallery_entries {
                file
            }
          }
          attributes {
            code
            value_index
            label
          }
        }
      }
    }
  }
}

